class ToggleLike{constructor(e){this.toggler=e,this.toggleLike()}toggleLike(){$(this.toggler).click(function(e){console.log("entered into toggle likes"),e.preventDefault();let l=this;$.ajax({type:"post",url:$(l).attr("href")}).done(function(e){let t=parseInt($(l).attr("data-likes"));console.log(t),1==e.data.deleted?--t:t+=1,$(l).attr("data-likes",t),$(l).html('<i class="fa-regular fa-heart"></i> '+t)}).fail(function(e){console.log("error in completing the request--\x3e",e.responseText)})})}}